var colors=["orange_white","orange","green_white","blue","blue_white","green","brown_white","brown"];var dragSrcEl=null;function dragStart(a){a.dataTransfer.effectAllowed="move";dragSrcEl=a.target;return true}function dragOver(a){if(a.preventDefault){a.preventDefault()}return false}function dragDrop(b){b.preventDefault();if(dragSrcEl!=b.target){var a=dragSrcEl.outerHTML;dragSrcEl.outerHTML=b.target.outerHTML;b.target.outerHTML=a}return false}var addText="";for(var i=0,j=colors.length;i<j;i++){var num=Math.floor(Math.random()*colors.length);addText+='<img src="images/'+colors[num]+'.png" id="'+colors[num]+'" draggable="true" ondragstart="return dragStart(event)" ondragover="return dragOver(event)" ondrop="dragDrop(event)" alt="'+colors[num]+'"style="padding:2px; border: 1px solid black;margin:1px;">';colors.splice(num,1)}document.getElementById("choices").innerHTML=addText;function checkAnswer(){if(document.getElementById("order").selectedIndex===0){var a=["orange_white","orange","green_white","blue","blue_white","green","brown_white","brown"]}else{var a=["green_white","green","orange_white","blue","blue_white","orange","brown_white","brown"]}var d=document.getElementById("choices");for(var c=0,b=a.length;c<b;c++){if(d.childNodes[c].id!==a[c]){document.getElementById("answer").innerHTML="That does not look like the correct order. Try again or see the correct order <a href='images/ethernet_wiring.png'>here</a>";return}}document.getElementById("answer").innerHTML="That is correct! Well done!"};